const issue = items[0].json;

// Build context for Claude Code
const context = {
  issue_title: issue.title,
  issue_description: issue.description,
  project_path: issue.project_path,
  error_logs: issue.error_logs || '',
  stack_trace: issue.stack_trace || '',
  affected_files: issue.affected_files || [],
  prompt_template: issue.prompt_template || 'Investigate this issue and provide a detailed report'
};

// Create investigation prompt
const prompt = `
${context.prompt_template}

Issue: ${context.issue_title}
Description: ${context.issue_description}
Project Path: ${context.project_path}

${context.error_logs ? 'Error Logs:\n' + context.error_logs : ''}
${context.stack_trace ? 'Stack Trace:\n' + context.stack_trace : ''}
${context.affected_files.length > 0 ? 'Affected Files: ' + context.affected_files.join(', ') : ''}

Please:
1. Analyze the codebase at the specified path
2. Identify the root cause of the issue
3. Provide a detailed investigation report
4. Suggest potential fixes
5. List any related issues or dependencies
`;

return [{json: {prompt, context, issue_id: issue.id}}];

const response = items[0].json;
const issue_id = items[0].json.issue_id;

// Parse Claude's response to extract structured data
const investigation_report = response.text || response.content || '';

// Extract root cause (look for patterns in response)
let root_cause = '';
const rootCauseMatch = investigation_report.match(/root cause[:\s]+([^\n]+)/i);
if (rootCauseMatch) {
  root_cause = rootCauseMatch[1].trim();
}

// Extract suggested fix
let suggested_fix = '';
const fixMatch = investigation_report.match(/suggested fix[:\s]+([^\n]+)/i);
if (fixMatch) {
  suggested_fix = fixMatch[1].trim();
}

// Extract affected files
const affectedFiles = [];
const filesMatch = investigation_report.match(/affected files[:\s]+([^\n]+)/i);
if (filesMatch) {
  affectedFiles.push(...filesMatch[1].split(',').map(f => f.trim()));
}

return [{json: {
  issue_id,
  investigation_report,
  root_cause,
  suggested_fix,
  affected_files: affectedFiles,
  timestamp: new Date().toISOString()
}}];
