steps.run:
echo '🚀 Calendar system initialized
  Dashboard: http://localhost:${UI_PORT}
  API: http://localhost:${API_PORT}
  CLI: calendar --help'

cd api && POSTGRES_EXPORTS=$(resource-postgres content get main | python3 -c "import sys, shlex; from urllib.parse import urlparse, urlunparse
conn_line = ''
for line in sys.stdin:
    if 'Connection String:' in line:
        conn_line = line.split('Connection String:', 1)[1].strip()
if not conn_line:
    sys.stderr.write('❌ Unable to determine Postgres credentials from resource-postgres\n')
    sys.exit(1)
parsed = urlparse(conn_line)
username = parsed.username or ''
password = parsed.password or ''
host = parsed.hostname or 'localhost'
port = parsed.port or 5432
auth = ''
if username:
    auth = username
    if password:
        auth += f':{password}'
    auth += '@'
netloc = f'{auth}{host}:{port}'
final = urlunparse(('postgres', netloc, '/calendar_system', '', 'sslmode=disable', ''))
def export(key, value):
    print(f'export {key}={shlex.quote(str(value))}')
export('POSTGRES_HOST', host)
export('POSTGRES_PORT', port)
if username:
    export('POSTGRES_USER', username)
if password:
    export('POSTGRES_PASSWORD', password)
export('POSTGRES_URL', final)
export('POSTGRES_DB', 'calendar_system')") || exit 1 && eval "$POSTGRES_EXPORTS" && ENVIRONMENT=test VROOLI_LIFECYCLE_MANAGED=true QDRANT_URL=http://localhost:6333 JWT_SECRET=development-secret-for-calendar-system AUTH_SERVICE_URL=http://localhost:15785 NOTIFICATION_SERVICE_URL=http://localhost:15310 NOTIFICATION_PROFILE_ID=calendar-system-prod ./calendar-api

echo '🚀 Services running:
  Dashboard: http://localhost:${UI_PORT}
  API: http://localhost:${API_PORT}
  CLI: calendar --help'
