// Test defaults for manual trigger
return {
  suggestion_ids: ['test_123'],
  dry_run: true,
  cleanup_scripts: [
    'echo "Would remove: /tmp/test.bak"',
    'echo "Would remove: /tmp/test~"'
  ]
};

// Validate and prepare cleanup execution
const input = $input.item.json;

// Extract parameters
const suggestionIds = Array.isArray(input.suggestion_ids) ? input.suggestion_ids : [];
const cleanupScripts = Array.isArray(input.cleanup_scripts) ? input.cleanup_scripts : [];
const dryRun = input.dry_run === true;
const force = input.force === true;

if (suggestionIds.length === 0 && cleanupScripts.length === 0) {
  throw new Error('No cleanup scripts or suggestion IDs provided');
}

// Generate execution ID
const executionId = 'cleanup_' + Date.now() + '_' + Math.random().toString(36).substring(2, 8);

// Prepare scripts for execution
const scriptsToRun = [];

for (const script of cleanupScripts) {
  // Add safety checks
  let safeScript = script;

  // Prevent dangerous operations
  if (script.includes('rm -rf /') || script.includes('rm -fr /')) {
    throw new Error('Dangerous cleanup script detected: ' + script);
  }

  // Add dry-run prefix if needed
  if (dryRun) {
    safeScript = 'echo "[DRY RUN] Would execute: ' + script.replace(/"/g, '\\"') + '"';
  }

  scriptsToRun.push({
    original: script,
    safe: safeScript,
    dry_run: dryRun
  });
}

return {
  execution_id: executionId,
  suggestion_ids: suggestionIds,
  scripts_to_run: scriptsToRun,
  dry_run: dryRun,
  force: force,
  timestamp: new Date().toISOString()
};

// Process cleanup result
const commandResult = $input.item.json;
const scriptInfo = $('Split in Batches').item.json;
const prepareInfo = $('Prepare Cleanup').item.json;

const success = commandResult.exitCode === 0;
const output = commandResult.stdout ? commandResult.stdout.trim() : '';
const error = commandResult.stderr ? commandResult.stderr.trim() : '';

// Parse files affected (if not dry run)
let filesModified = [];
let filesDeleted = [];
let bytesFreed = 0;

if (!scriptInfo.dry_run && success) {
  // Try to extract file information from output
  const fileMatches = output.match(/removed?[:\s]+'?([^']+)'?/gi) || [];
  filesDeleted = fileMatches.map(m => m.replace(/removed?[:\s]+/i, '').replace(/'/g, ''));
}

return {
  script: scriptInfo.original,
  success: success,
  dry_run: scriptInfo.dry_run,
  output: output,
  error: error,
  exit_code: commandResult.exitCode,
  files_modified: filesModified,
  files_deleted: filesDeleted,
  bytes_freed: bytesFreed,
  executed_at: new Date().toISOString()
};

// Combine all cleanup results
const allResults = $input.all();
const prepareInfo = $('Prepare Cleanup').item.json;

let totalExecuted = 0;
let totalSuccessful = 0;
let totalFailed = 0;
let allFilesDeleted = [];
let allFilesModified = [];
let totalBytesFreed = 0;
let errors = [];
let results = [];

for (const result of allResults) {
  const data = result.json;
  totalExecuted++;

  if (data.success) {
    totalSuccessful++;
  } else {
    totalFailed++;
    if (data.error) {
      errors.push({
        script: data.script,
        error: data.error
      });
    }
  }

  allFilesDeleted = allFilesDeleted.concat(data.files_deleted);
  allFilesModified = allFilesModified.concat(data.files_modified);
  totalBytesFreed += data.bytes_freed;

  results.push({
    script: data.script,
    success: data.success,
    output: data.output,
    files_affected: data.files_deleted.concat(data.files_modified)
  });
}

return {
  success: totalFailed === 0,
  execution_id: prepareInfo.execution_id,
  dry_run: prepareInfo.dry_run,
  started_at: prepareInfo.timestamp,
  completed_at: new Date().toISOString(),
  statistics: {
    total_executed: totalExecuted,
    successful: totalSuccessful,
    failed: totalFailed,
    files_deleted: allFilesDeleted.length,
    files_modified: allFilesModified.length,
    bytes_freed: totalBytesFreed
  },
  results: results,
  errors: errors
};
