#!/bin/bash\nset -euo pipefail\n\necho "=== Code Structure Verification ==="\n\n# Check required directories\nrequired_dirs=(api ui cli test .vrooli initialization)\nfor dir in "${required_dirs[@]}"; do\n  if [ ! -d "../../$dir" ]; then\n    echo "❌ Missing directory: $dir"\n    exit 1\n  fi\ndone\n\n# Check key files\nrequired_files=(\n  "api/main.go"\n  "ui/index.html"\n  ".vrooli/service.json"\n  "cli/prompt-injection-arena"\n)\nfor file in "${required_files[@]}"; do\n  if [ ! -f "../../$file" ]; then\n    echo "❌ Missing file: $file"\n    exit 1\n  fi\ndone\n\necho "✅ Structure verified"