#!/bin/bash\nset -euo pipefail\n\necho "=== Unit Tests for Prompt Injection Arena ==="\n\n# Go API unit tests\nif [ -d "../../api" ]; then\n  (cd ../../api &amp;&amp; go test ./... -v -short) || exit 1\nfi\n\n# UI unit tests (if Jest or similar configured)\nif [ -d "../../ui" ] &amp;&amp; [ -f "../../ui/package.json" ]; then\n  (cd ../../ui &amp;&amp; npm test -- --passWithNoTests) || exit 1\nfi\n\n# CLI bats tests\nif [ -d "../../cli" ] &amp;&amp; ls ../../cli/*.bats 1&gt; /dev/null 2&gt;&amp;1; then\n  (cd ../../cli &amp;&amp; bats *.bats) || exit 1\nfi\n\necho "âœ… Unit tests passed"