// Test defaults for manual trigger
return {
  job_id: 'test-job-001',
  candidate_name: 'John Doe',
  resume_text: 'Senior Software Engineer with 10 years experience in full-stack development. Expertise in React, Node.js, Python, and cloud technologies. Led teams of 5-10 developers on multiple successful projects.',
  file_name: 'john_doe_resume.pdf',
  file_type: 'pdf'
};

// Input validation and sanitization
const input = $input.first().json;
const errors = [];
const warnings = [];

// Validate job_id
if (!input.job_id || typeof input.job_id !== 'string') {
  errors.push('Invalid or missing job_id');
}

// Validate candidate info
if (!input.candidate_name && !input.resume_text) {
  errors.push('Missing candidate information');
}

// File validation if binary data present
if ($input.first().binary?.data) {
  const binaryData = $input.first().binary.data;
  const allowedTypes = ['.pdf', '.docx', '.doc', '.txt'];
  const fileName = binaryData.fileName || '';
  const ext = fileName.toLowerCase().match(/\.[^.]+$/);

  if (ext && !allowedTypes.includes(ext[0])) {
    errors.push(`Invalid file type: ${ext[0]}`);
  }

  // Size check (10MB limit)
  if (binaryData.data && Buffer.byteLength(binaryData.data, 'base64') > 10485760) {
    errors.push('File exceeds 10MB limit');
  }
}

if (errors.length > 0) {
  throw new Error(`Validation failed: ${errors.join(', ')}`);
}

return {
  valid: true,
  job_id: input.job_id,
  candidate_name: input.candidate_name || 'Unknown',
  resume_text: input.resume_text || '',
  file_info: {
    name: input.file_name || 'resume.pdf',
    type: input.file_type || 'unknown'
  },
  timestamp: new Date().toISOString()
};

// Prepare response
const candidateData = $('Store in Database').item.json;
const extractedInfo = $('Extract Resume Info').item.json.response;
const embeddingStored = $('Store in Qdrant').item.json.result === 'ok';

return {
  success: true,
  candidate_id: candidateData.id,
  message: 'Resume processed successfully',
  details: {
    candidate_name: $('Validate Input').item.json.candidate_name,
    job_id: $('Validate Input').item.json.job_id,
    skills_extracted: extractedInfo.skills?.length || 0,
    experience_years: extractedInfo.experience_years || 0,
    embedding_stored: embeddingStored,
    timestamp: $('Validate Input').item.json.timestamp
  }
};
