// Parse Claude's planning response
const claudeOutput = $json.stdout || $json.output || '';
const request = $json;

if (!claudeOutput.trim()) {
  throw new Error('Claude Code returned empty response');
}

// Extract structured information from the plan
const planAnalysis = {
  has_architecture: claudeOutput.includes('Architecture') || claudeOutput.includes('architecture'),
  has_resources: claudeOutput.includes('Resource') || claudeOutput.includes('resource'),
  has_phases: claudeOutput.includes('Phase') || claudeOutput.includes('phase'),
  has_file_structure: claudeOutput.includes('File') || claudeOutput.includes('structure'),
  completeness_score: 0
};

// Calculate completeness score
planAnalysis.completeness_score = [
  planAnalysis.has_architecture,
  planAnalysis.has_resources,
  planAnalysis.has_phases,
  planAnalysis.has_file_structure
].filter(Boolean).length * 25;

// Determine if plan needs refinement
const needsRefinement = planAnalysis.completeness_score < 75 && 
                       request.iteration < request.max_iterations;

return {
  ...request,
  plan_output: claudeOutput,
  plan_analysis: planAnalysis,
  needs_refinement: needsRefinement,
  plan_quality: planAnalysis.completeness_score >= 75 ? 'good' : 
               planAnalysis.completeness_score >= 50 ? 'acceptable' : 'needs_work'
};

// Generate refinement prompt for improving the plan
const request = $json;

const refinementPrompt = `# Scenario Generation - Plan Refinement (Iteration ${request.iteration})

The initial plan needs improvement. Please refine the following plan based on the analysis:

## Current Plan:
${request.plan_output}

## Quality Analysis:
- Architecture Coverage: ${request.plan_analysis.has_architecture ? '✓' : '✗'}
- Resource Planning: ${request.plan_analysis.has_resources ? '✓' : '✗'}
- Implementation Phases: ${request.plan_analysis.has_phases ? '✓' : '✗'}
- File Structure: ${request.plan_analysis.has_file_structure ? '✓' : '✗'}
- Completeness Score: ${request.plan_analysis.completeness_score}%

## Refinement Focus:
${!request.plan_analysis.has_architecture ? '- Add detailed architecture overview with component relationships\n' : ''}
${!request.plan_analysis.has_resources ? '- Specify required Vrooli resources and external dependencies\n' : ''}
${!request.plan_analysis.has_phases ? '- Break down implementation into clear phases with dependencies\n' : ''}
${!request.plan_analysis.has_file_structure ? '- Provide complete file and directory structure\n' : ''}

## Additional Requirements:
- Ensure all sections are comprehensive and actionable
- Add specific technical details where missing
- Include risk assessment and mitigation strategies
- Provide clear success criteria for each phase

Please provide an improved, more detailed plan that addresses these gaps.

**Output Format:** Well-structured markdown with enhanced detail in weak areas.`;

return {
  ...request,
  prompt: refinementPrompt,
  iteration: request.iteration + 1
};

// Finalize planning phase with completed plan
const request = $json;
const finalPlan = request.plan_output;

// Extract resource requirements from plan
const resourcesMatch = finalPlan.match(/(?:resource|Resource)[^\n]*?(?:postgres|n8n|windmill|ollama|minio|redis|vault|comfyui|unstructured-io|judge0|browserless)[^\n]*/gi);
const identifiedResources = resourcesMatch ? 
  [...new Set(resourcesMatch.join(' ').match(/postgres|n8n|windmill|ollama|minio|redis|vault|comfyui|unstructured-io|judge0|browserless/gi))] :
  ['postgres', 'n8n']; // defaults

// Extract architecture components
const architectureMatch = finalPlan.match(/(?:component|Component|service|Service)[^\n]*[^\n]*/gi);
const architectureComponents = architectureMatch ? architectureMatch.slice(0, 10) : [];

return {
  ...request,
  status: 'planning_complete',
  plan_final: finalPlan,
  resources: identifiedResources,
  architecture: {
    components: architectureComponents,
    has_api: finalPlan.toLowerCase().includes('api'),
    has_ui: finalPlan.toLowerCase().includes('ui') || finalPlan.toLowerCase().includes('interface'),
    has_database: finalPlan.toLowerCase().includes('database') || finalPlan.toLowerCase().includes('postgres'),
    has_workflows: finalPlan.toLowerCase().includes('workflow') || finalPlan.toLowerCase().includes('n8n')
  },
  planning_metrics: {
    total_iterations: request.iteration,
    final_quality_score: request.plan_analysis?.completeness_score || 100,
    resources_identified: identifiedResources.length,
    plan_length: finalPlan.length
  },
  completed_at: new Date().toISOString()
};
