return await main('process_episode', {
  id: ctx.episode_id,
  title: ctx.episode_title,
  description: ctx.episode_description,
  audio_file: ctx.audio_file,
  language: ctx.language,
  metadata: {
    file_size: ctx.file_size || 50000000,
    format: 'mp3',
    quality: 'high',
    channels: 1
  }
});

const contentRequest = {
  episode_id: ctx.content_source,
  transcript: ctx.content_source,
  content_strategy: {
    primary_goal: 'engagement',
    content_pillars: ['Education', 'Insights'],
    messaging_themes: ['Innovation', 'Growth'],
    content_frequency: 'weekly',
    repurposing_ratio: 5
