  brand_profile: {
    brand_name: 'Podcast Brand',
    industry: 'Media',
    brand_voice: ctx.brand_voice || 'professional',
    tone_attributes: ['Professional', 'Engaging'],
    key_messages: ['Quality content', 'Expert insights'],
    vocabulary_preferences: {},
    content_guidelines: ['Clear', 'Actionable']
  },
  target_audiences: [{
    segment_name: ctx.target_audience || 'General Audience',
    demographics: {
      age_range: '25-45',
      profession: 'Professionals',
      experience_level: 'Intermediate',
      interests: ['Technology', 'Business']
    },
    pain_points: ['Time management', 'Staying informed'],
    preferred_content_types: ctx.content_types || ['summary'],
    communication_style: 'Professional',
    content_consumption_patterns: ['Quick reads']
  }],
  distribution_channels: ['blog', 'linkedin', 'twitter']
};

return await main('generate_content_package', contentRequest);

