    pain_points: [],
    preferred_content_types: [],
    communication_style: 'Professional',
    content_consumption_patterns: []
  }],
  distribution_channels: ['linkedin', 'twitter', 'blog']
};

return await main('generate_content_calendar', request);

const brandProfile = {
  brand_name: ctx.brand_name || 'Brand',
  industry: ctx.brand_industry || 'media',
  brand_voice: ctx.brand_voice_select || 'educational',
  tone_attributes: ['Professional', 'Engaging'],
  key_messages: (ctx.brand_messages || '').split('\n').filter(m => m.trim()),
  vocabulary_preferences: {},
  content_guidelines: (ctx.brand_guidelines || '').split('\n').filter(g => g.trim())
};

return await main('analyze_brand_consistency', null, null, null, null, brandProfile, ctx.content_to_analyze);
