export default async ({ page, params, context }) => {
    const url = params.url || 'https://httpbin.org/html';
    const width = parseInt(params.width) || 1920;
    const height = parseInt(params.height) || 1080;

    // Set viewport
    await page.setViewport({ width, height });

    // Navigate to URL
    await page.goto(url, {
        waitUntil: 'networkidle2',
        timeout: 30000
    });

    // Take screenshot
    const screenshot = await page.screenshot({
        fullPage: params.fullPage === 'true' || params.fullPage === true,
        type: 'png'
    });

    // Get page info
    const title = await page.title();
    const finalUrl = page.url();

    return {
        success: true,
        url: finalUrl,
        title: title,
        screenshot: {
            width: width,
            height: height,
            fullPage: params.fullPage === 'true' || params.fullPage === true,
            size: screenshot.length
        },
        timestamp: new Date().toISOString(),
        context: context
    };
};
