const review = JSON.parse($node['Initial Self-Review'].json.response);

// Calculate introspection metrics
const avgConfidence = {
  reasoning: review.initial_reasoning?.confidence || 50,
  bias_awareness: review.bias_detection?.confidence || 50,
  knowledge_completeness: review.knowledge_gaps?.confidence || 50,
  alternative_consideration: review.alternative_approaches?.confidence || 50
};

const overallConfidence = Object.values(avgConfidence).reduce((sum, conf) => sum + conf, 0) / 4;

// Identify areas needing deeper review
const concernAreas = Object.entries(avgConfidence)
  .filter(([area, conf]) => conf < 70)
  .map(([area]) => area);

// Determine if additional review iterations are needed
const needsAdditionalReview = overallConfidence < 75 || concernAreas.length > 2;

return {
  json: {
    initial_review: review,
    introspection_metrics: {
      confidence_scores: avgConfidence,
      overall_confidence: Math.round(overallConfidence),
      concern_areas: concernAreas,
      needs_additional_review: needsAdditionalReview,
      iteration_count: 1
    },
    timestamp: new Date().toISOString()
  }
};

const initialReview = $node['Analyze Initial Review'].json;
const deeperReview = $node['Deeper Critical Review']?.json || null;
const finalAssessment = $node['Final Assessment']?.json || {};

// Compile complete self-review results
const selfReviewResults = {
  original_decision: $json.decision,
  review_process: {
    initial_review: initialReview.initial_review,
    initial_confidence: initialReview.introspection_metrics.overall_confidence,
    deeper_review_needed: initialReview.introspection_metrics.needs_additional_review,
    deeper_review: deeperReview ? JSON.parse(deeperReview.response) : null,
    final_assessment: finalAssessment.response ? JSON.parse(finalAssessment.response) : {}
  },
  learning_outcomes: {
    confidence_evolution: {
      initial: initialReview.introspection_metrics.overall_confidence,
      final: finalAssessment.response ? JSON.parse(finalAssessment.response).overall_confidence : initialReview.introspection_metrics.overall_confidence
    },
    key_insights: finalAssessment.response ? JSON.parse(finalAssessment.response).key_insights : [],
    areas_improved: initialReview.introspection_metrics.concern_areas,
    iteration_count: deeperReview ? 2 : 1
  },
  timestamp: new Date().toISOString(),
  model_used: $json.model || 'codellama'
};

return {
  json: selfReviewResults
};
