const response = JSON.parse($node['Generate Pros and Cons'].json.response);

// Calculate weighted scores
const prosScore = response.pros.reduce((sum, p) => sum + p.weight, 0);
const consScore = response.cons.reduce((sum, c) => sum + c.weight, 0);
const netScore = prosScore - consScore;

return {
  json: {
    analysis: response,
    scores: {
      pros_total: prosScore,
      cons_total: consScore,
      net_score: netScore,
      recommendation_strength: Math.abs(netScore) / (prosScore + consScore)
    },
    timestamp: new Date().toISOString(),
    model_used: $json.model || 'llama3.2'
  }
};
