parameters.jsCode:
const scheduleData = $json;
const userId = $('merge_triggers').item.json.user_id;
const weekStart = $('merge_triggers').item.json.week_start;
const chores = $('fetch_chores').all().map(c => c.json);

// Parse AI response
let schedule;
try {
  schedule = JSON.parse(scheduleData.response).schedule;
} catch (e) {
  // Fallback to simple distribution if AI fails
  schedule = generateFallbackSchedule(chores);
}

// Create assignments for each scheduled chore
const assignments = [];
const startDate = new Date(weekStart);

schedule.forEach((day, index) => {
  const assignDate = new Date(startDate);
  assignDate.setDate(startDate.getDate() + index);

  day.chores.forEach(choreId => {
    const chore = chores.find(c => c.id === choreId);
    if (chore) {
      assignments.push({
        chore_id: choreId,
        user_id: userId,
        assigned_date: assignDate.toISOString().split('T')[0],
        due_date: assignDate.toISOString().split('T')[0],
        status: 'pending',
        base_points: chore.base_points
      });
    }
  });
});

function generateFallbackSchedule(chores) {
  // Simple round-robin distribution
  const days = ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sunday'];
  const schedule = days.map(day => ({ day, chores: [], total_time: 0 }));

  chores.forEach((chore, index) => {
    const dayIndex = index % 7;
    schedule[dayIndex].chores.push(chore.id);
    schedule[dayIndex].total_time += chore.estimated_minutes;
  });

  return schedule;
}

return {
  assignments,
  week_start: weekStart,
  total_assignments: assignments.length
};
