// Default values for manual testing
const defaults = {
  ingredients: "soy milk, tofu, nutritional yeast, olive oil"
};

return [{json: defaults}];

// Merge webhook or default data
const isManual = $('Manual Trigger').first() !== undefined;
const input = isManual ? $json : $('Webhook').first().json;

// Get ingredients from input
const ingredients = input.ingredients || 'unknown ingredients';

// Prepare the prompt for vegan analysis
const prompt = `You are a vegan food expert. Analyze these ingredients and determine if the food is vegan. Be precise and thorough.

Ingredients: ${ingredients}

Provide:
1. Whether the food is vegan (yes/no)
2. List any non-vegan ingredients found
3. Brief explanation of why each non-vegan ingredient isn't vegan

Format your response as JSON:
{"isVegan": boolean, "nonVeganIngredients": ["ingredient1", "ingredient2"], "explanations": {"ingredient1": "reason", "ingredient2": "reason"}}`;

// Prepare request for Ollama CLI
return [{
  json: {
    prompt: prompt,
    model: 'llama3.2:3b',
    type: 'reasoning',
    quiet: true,
    timeout_seconds: 30,
    original_ingredients: ingredients
  }
}];

// Parse the response from shared Ollama workflow
const response = $json;
const ingredients = $node["prepare_request"].json.original_ingredients;

// Get the response text from the shared workflow
const responseText = response.response || response.content || '';
const success = response.success !== false;
const error = response.error || null;

let result;
try {
  // Try to parse as JSON
  if (responseText && typeof responseText === 'string') {
    // Clean up the response (remove markdown code blocks if present)
    const cleanedResponse = responseText.replace(/```json\n?/g, '').replace(/```\n?/g, '').trim();
    result = JSON.parse(cleanedResponse);
  } else {
    throw new Error('Empty response from Ollama');
  }
} catch (e) {
  // Fallback parsing if not valid JSON
  const text = responseText.toLowerCase();

  // Look for common non-vegan ingredients
  const nonVeganKeywords = ['milk', 'egg', 'honey', 'gelatin', 'whey', 'casein', 'lactose', 'butter', 'cheese', 'meat', 'chicken', 'beef', 'pork', 'fish'];
  const foundNonVegan = [];

  for (const keyword of nonVeganKeywords) {
    if (text.includes(keyword)) {
      foundNonVegan.push(keyword);
    }
  }

  result = {
    isVegan: foundNonVegan.length === 0,
    nonVeganIngredients: foundNonVegan,
    explanations: {},
    rawAnalysis: responseText,
    error: e.message
  };
}

// Format final response
return [{
  json: {
    ingredients: ingredients,
    isVegan: result.isVegan || false,
    nonVeganIngredients: result.nonVeganIngredients || [],
    explanations: result.explanations || {},
    analysis: result.rawAnalysis || responseText || 'Analysis complete',
    timestamp: new Date().toISOString(),
    confidence: result.isVegan !== undefined ? 'high' : 'medium',
    execution_details: {
      model_used: response.execution_meta?.model || 'llama3.2:3b',
      workflow_success: success,
      shared_workflow_used: true
    }
  }
}];
