// Calculate BMR using Mifflin-St Jeor Equation
const age = parseFloat($input.item.json.age);
const weight_kg = parseFloat($input.item.json.weight_kg);
const height_cm = parseFloat($input.item.json.height_cm);
const gender = $input.item.json.gender;
const activity_level = $input.item.json.activity_level;
const weight_goal = $input.item.json.weight_goal;

let bmr;
if (gender === 'male') {
  bmr = (10 * weight_kg) + (6.25 * height_cm) - (5 * age) + 5;
} else {
  bmr = (10 * weight_kg) + (6.25 * height_cm) - (5 * age) - 161;
}

// Activity multipliers
const activity_multipliers = {
  sedentary: 1.2,
  lightly_active: 1.375,
  moderately_active: 1.55,
  very_active: 1.725,
  extremely_active: 1.9
};

const tdee = Math.round(bmr * (activity_multipliers[activity_level] || 1.55));

// Weight goal adjustments
let daily_calories = tdee;
let calorie_adjustment = 0;

if (weight_goal === 'lose') {
  calorie_adjustment = -500; // 0.5 kg per week loss
  daily_calories = tdee - 500;
} else if (weight_goal === 'gain') {
  calorie_adjustment = 300; // 0.3 kg per week gain
  daily_calories = tdee + 300;
}

// Calculate macro recommendations (moderate balanced approach)
const protein_percent = weight_goal === 'gain' ? 0.30 : 0.25;
const carbs_percent = 0.45;
const fat_percent = 0.30;

const protein_calories = daily_calories * protein_percent;
const carbs_calories = daily_calories * carbs_percent;
const fat_calories = daily_calories * fat_percent;

const protein_grams = Math.round(protein_calories / 4);
const carbs_grams = Math.round(carbs_calories / 4);
const fat_grams = Math.round(fat_calories / 9);

// Minimum protein recommendation
const min_protein = Math.round(weight_kg * 1.6); // 1.6g per kg for active individuals

return {
  bmr: Math.round(bmr),
  tdee: tdee,
  daily_calories: daily_calories,
  calorie_adjustment: calorie_adjustment,
  macros: {
    protein_grams: Math.max(protein_grams, min_protein),
    carbs_grams: carbs_grams,
    fat_grams: fat_grams,
    fiber_grams: Math.round(daily_calories / 100), // 10-14g per 1000 calories
    sugar_limit: Math.round(daily_calories * 0.10 / 4), // 10% of calories max
    sodium_limit: 2300 // mg per day
  },
  water_ml: Math.round(weight_kg * 35), // 35ml per kg body weight
  notes: `Based on ${gender}, ${age} years, ${weight_kg}kg, ${height_cm}cm, ${activity_level} activity, goal: ${weight_goal}`
};
