// Parse and validate the response from HTTP request
const httpResponse = $json;
const originalInput = $('Prepare Prompt').first().json.original_input;

try {
  // Extract the actual response from the ollama HTTP response
  let ollamaResponse = httpResponse.response;

  // Parse the JSON response
  let nutritionData = {};

  if (typeof ollamaResponse === 'string') {
    nutritionData = JSON.parse(ollamaResponse);
  } else {
    nutritionData = ollamaResponse;
  }

  // Ensure all required fields with defaults
  const validatedData = {
    food_description: originalInput.food_description,
    meal_type: originalInput.meal_type,
    user_id: originalInput.user_id,
    calories: nutritionData.calories || 0,
    protein: nutritionData.protein || 0,
    carbs: nutritionData.carbs || 0,
    fat: nutritionData.fat || 0,
    fiber: nutritionData.fiber || 0,
    sugar: nutritionData.sugar || 0,
    sodium: nutritionData.sodium || 0,
    serving_size: nutritionData.serving_size || '1 serving',
    food_category: nutritionData.food_category || 'Unknown',
    dietary_tags: nutritionData.dietary_tags || [],
    nutrients: nutritionData.nutrients || [],
    analyzed_at: new Date().toISOString()
  };

  return [{ json: validatedData }];

} catch (error) {
  // Return default values if parsing fails
  return [{
    json: {
      food_description: originalInput.food_description,
      meal_type: originalInput.meal_type,
      user_id: originalInput.user_id,
      calories: 0,
      protein: 0,
      carbs: 0,
      fat: 0,
      fiber: 0,
      sugar: 0,
      sodium: 0,
      serving_size: '1 serving',
      food_category: 'Unknown',
      dietary_tags: [],
      nutrients: [],
      error: error.message,
      analyzed_at: new Date().toISOString()
    }
  }];
}
