try {
  const response = $input.first().json;

  // Extract JSON from the response
  let paletteData;

  // Try to parse the response directly if it's already JSON
  if (response.response) {
    const text = response.response;
    const jsonMatch = text.match(/\{[\s\S]*\}/g);

    if (jsonMatch) {
      paletteData = JSON.parse(jsonMatch[0]);
    } else {
      throw new Error('No JSON found in response');
    }
  } else if (response.palette) {
    // Already in correct format
    paletteData = response;
  } else {
    throw new Error('Unexpected response format');
  }

  // Validate palette data
  if (!paletteData.palette || !Array.isArray(paletteData.palette)) {
    throw new Error('Invalid palette format');
  }

  // Ensure all colors are valid hex codes
  const validatedPalette = paletteData.palette.map(color => {
    if (!/^#[0-9A-F]{6}$/i.test(color)) {
      // Try to fix common issues
      if (color.startsWith('#') && color.length === 7) {
        return color.toUpperCase();
      }
      throw new Error(`Invalid hex color: ${color}`);
    }
    return color.toUpperCase();
  });

  return {
    success: true,
    palette: validatedPalette,
    name: paletteData.name || 'Generated Palette',
    theme: $node['Input Router'].json.theme,
    style: $node['Input Router'].json.style || 'balanced',
    description: paletteData.description || 'AI-generated color palette',
    harmony_type: paletteData.harmony_type || 'custom',
    primary: paletteData.primary || validatedPalette[0],
    secondary: paletteData.secondary || validatedPalette[1],
    accent: paletteData.accent || validatedPalette[validatedPalette.length - 1],
    metadata: {
      generated_at: new Date().toISOString(),
      num_colors: validatedPalette.length,
      model: 'llama3.2'
    }
  };
} catch (error) {
  // Return a fallback palette on error
  const fallbackPalettes = {
    'vibrant': ['#FF6B6B', '#4ECDC4', '#45B7D1', '#96CEB4', '#FFEAA7'],
    'pastel': ['#FFD3E1', '#C9F0FF', '#FFE5CC', '#E8D5FF', '#D4F1D4'],
    'dark': ['#2C3E50', '#34495E', '#7F8C8D', '#95A5A6', '#BDC3C7'],
    'earthy': ['#8B4513', '#A0522D', '#CD853F', '#DEB887', '#F5DEB3'],
    'minimal': ['#000000', '#333333', '#666666', '#999999', '#CCCCCC']
  };

  const style = $node['Input Router'].json.style || 'vibrant';
  const palette = fallbackPalettes[style] || fallbackPalettes.vibrant;

  return {
    success: true,
    palette: palette,
    name: 'Fallback ' + style.charAt(0).toUpperCase() + style.slice(1),
    theme: $node['Input Router'].json.theme || 'default',
    style: style,
    description: 'Pre-defined fallback palette',
    harmony_type: 'curated',
    primary: palette[0],
    secondary: palette[1],
    accent: palette[palette.length - 1],
    metadata: {
      generated_at: new Date().toISOString(),
      num_colors: palette.length,
      fallback: true,
      error: error.message
    }
  };
}
