// Parse JSON output from psql command
const output = $input.item.json.stdout || '[]';
try {
  const data = JSON.parse(output);
  return { json: data };
} catch (e) {
  console.error('Failed to parse history data:', e);
  return { json: [] };
}

// Parse JSON output from psql command
const output = $input.item.json.stdout || '{}';
try {
  const data = JSON.parse(output);
  return { json: data };
} catch (e) {
  console.error('Failed to parse stats data:', e);
  return { json: {} };
}

const history = $input.all()[0].json;
const stats = $input.all()[1]?.json || {};

return [{
  json: {
    history: history,
    statistics: {
      total_spins: stats.total_spins || 0,
      days_active: stats.days_active || 0,
      most_common: stats.most_common || 'N/A',
      frequency: stats.frequency || 0
    },
    session_id: $node["set_params"].json.session_id,
    timestamp: new Date().toISOString()
  }
}];
