// Extract important SEO elements from scraped content
const content = $json.stdout || '';
const url = $('default-values').item.json.url || $('webhook-trigger').item.json.url;

// Extract title
const titleMatch = content.match(/<title[^>]*>([^<]*)<\/title>/i);
const title = titleMatch ? titleMatch[1].trim() : '';

// Extract meta description
const metaDescMatch = content.match(/<meta[^>]*name=["']description["'][^>]*content=["']([^"']*)["']/i);
const metaDescription = metaDescMatch ? metaDescMatch[1].trim() : '';

// Count headers
const h1Count = (content.match(/<h1[^>]*>/gi) || []).length;
const h2Count = (content.match(/<h2[^>]*>/gi) || []).length;
const h3Count = (content.match(/<h3[^>]*>/gi) || []).length;

// Count images and check for alt tags
const imgMatches = content.match(/<img[^>]*>/gi) || [];
const totalImages = imgMatches.length;
const imagesWithAlt = imgMatches.filter(img => /alt=["'][^"']+["']/i.test(img)).length;

// Count links
const linkMatches = content.match(/<a[^>]*href=["'][^"']*["'][^>]*>/gi) || [];
const totalLinks = linkMatches.length;
const internalLinks = linkMatches.filter(link => !/https?:\/\//i.test(link) || link.includes(url)).length;
const externalLinks = totalLinks - internalLinks;

// Clean content for analysis (remove HTML tags)
const textContent = content.replace(/<[^>]*>/g, ' ').replace(/\s+/g, ' ').trim();
const wordCount = textContent.split(' ').filter(word => word.length > 0).length;

return {
  url: url,
  extracted_data: {
    title: title,
    title_length: title.length,
    meta_description: metaDescription,
    meta_description_length: metaDescription.length,
    h1_count: h1Count,
    h2_count: h2Count,
    h3_count: h3Count,
    total_images: totalImages,
    images_with_alt: imagesWithAlt,
    total_links: totalLinks,
    internal_links: internalLinks,
    external_links: externalLinks,
    word_count: wordCount,
    content_sample: textContent.substring(0, 500)
  }
};

