// Parse the AI response and format final results
const aiResponse = $json.response || '{}';
let seoAudit;

try {
  seoAudit = typeof aiResponse === 'string' ? JSON.parse(aiResponse) : aiResponse;
} catch (e) {
  // If parsing fails, create a basic structure
  seoAudit = {
    overall_score: 50,
    error: 'Failed to parse AI response',
    raw_response: aiResponse
  };
}

const extractedData = $('extract-seo-elements').item.json.extracted_data;
const url = $('extract-seo-elements').item.json.url;

return {
  audit_id: Date.now().toString(),
  url: url,
  timestamp: new Date().toISOString(),
  seo_audit: seoAudit,
  raw_data: extractedData,
  success: true
};
