// Provide test defaults for manual testing
return {
  file_id: 'test-image-001',
  image_path: '/test/sample-image.jpg',
  mime_type: 'image/jpeg',
  analyze_options: {
    extract_text: true,
    detect_objects: true,
    generate_tags: true,
    suggest_organization: true
  }
};

// Parse vision model response and structure data
const analysis = $json;
const fileData = $('Get Image Metadata').first().file_data[0];

// Parse the LLM response (assuming it returns JSON-like structure)
let parsedAnalysis;
try {
  parsedAnalysis = typeof analysis.response === 'string' ? JSON.parse(analysis.response) : analysis.response;
} catch (e) {
  // Fallback parsing if not proper JSON
  parsedAnalysis = {
    description: analysis.response || 'Unable to analyze',
    objects: [],
    scene_type: 'unknown',
    text_detected: false,
    tags: [],
    quality: 'medium'
  };
}

return {
  file_id: fileData.file_id,
  file_path: fileData.file_path,
  analysis: {
    description: parsedAnalysis.description,
    detected_objects: parsedAnalysis.objects || [],
    scene_type: parsedAnalysis.scene_type || 'unknown',
    has_text: parsedAnalysis.text_detected || false,
    extracted_text: parsedAnalysis.extracted_text || '',
    suggested_tags: parsedAnalysis.tags || [],
    quality_score: calculateQualityScore(parsedAnalysis),
    confidence: 0.85
  },
  suggestions: generateOrganizationSuggestions(parsedAnalysis, fileData),
  timestamp: new Date().toISOString()
};

function calculateQualityScore(analysis) {
  let score = 0.5;
  if (analysis.quality === 'high') score = 0.9;
  else if (analysis.quality === 'low') score = 0.3;
  if (analysis.objects?.length > 3) score += 0.1;
  if (analysis.text_detected) score += 0.1;
  return Math.min(score, 1.0);
}

function generateOrganizationSuggestions(analysis, fileData) {
  const suggestions = [];

  // Suggest folder based on content
  if (analysis.objects?.includes('person') || analysis.objects?.includes('face')) {
    suggestions.push({
      type: 'folder',
      value: '/Personal/Photos',
      reason: 'Contains people'
    });
  }

  if (analysis.scene_type === 'document' || analysis.scene_type === 'screenshot') {
    suggestions.push({
      type: 'folder',
      value: '/Documents/Images',
      reason: 'Document or screenshot'
    });
  }

  if (analysis.scene_type === 'outdoor') {
    suggestions.push({
      type: 'folder',
      value: '/Photos/Nature',
      reason: 'Outdoor scene'
    });
  }

  // Add suggested tags
  (analysis.tags || []).forEach(tag => {
    suggestions.push({
      type: 'tag',
      value: tag,
      reason: 'AI-suggested tag'
    });
  });

  return suggestions;
}
