return [{
  json: {
    user_id: items[0]?.json?.user_id || 'test-user',
    subject_id: items[0]?.json?.subject_id || null,
    time_period: items[0]?.json?.time_period || '7d',
    analysis_type: items[0]?.json?.analysis_type || 'comprehensive'
  }
}];

const stats = $input.all()[0].json;
const aiInsights = $input.all()[1].json.response || {};

let insights;
try {
  insights = typeof aiInsights === 'string' ? JSON.parse(aiInsights) : aiInsights;
} catch (e) {
  // Fallback insights
  insights = {
    analysis: "Your study pattern shows dedication with room for consistency improvements.",
    strengths: [
      "Regular engagement with study materials",
      "Good quiz completion rate"
    ],
    improvements: [
      "Increase average session duration",
      "Review flashcards more frequently"
    ],
    recommendations: [
      "Try the Pomodoro technique for longer focus sessions",
      "Review flashcards daily for 10 minutes",
      "Take practice quizzes before each study session",
      "Set specific daily study goals"
    ],
    next_topics: [
      "Advanced concepts in your current subject",
      "Practice problems and applications"
    ],
    motivation: "Keep up the great work! Every study session brings you closer to mastery."
  };
}

// Calculate progress metrics
const progressScore = Math.round(
  (stats.total_sessions * 2 + 
   (stats.avg_score || 0) + 
   (stats.avg_retention || 0)) / 4
);

const streakDays = stats.study_days || 0;
const level = Math.floor(progressScore / 20) + 1;

return [{
  json: {
    success: true,
    user_id: $input.first().json.user_id,
    period: $input.first().json.time_period,
    statistics: {
      study_sessions: stats.total_sessions || 0,
      total_minutes: stats.total_minutes || 0,
      avg_session_minutes: stats.avg_session_length || 0,
      study_days: stats.study_days || 0,
      consistency: stats.study_consistency || 'Inactive',
      quizzes_taken: stats.quizzes_taken || 0,
      avg_quiz_score: stats.avg_score || 0,
      cards_reviewed: stats.cards_reviewed || 0,
      cards_mastered: stats.cards_mastered || 0,
      retention_rate: stats.avg_retention || 0
    },
    insights: insights,
    gamification: {
      level: level,
      progress_score: progressScore,
      streak_days: streakDays,
      badges: [
        streakDays >= 7 ? "Week Warrior" : null,
        stats.cards_mastered >= 50 ? "Flashcard Master" : null,
        stats.avg_score >= 80 ? "Quiz Champion" : null
      ].filter(b => b !== null)
    },
    generated_at: new Date().toISOString()
  }
}];
