// Provide defaults for manual testing
const defaults = {
  user_id: 'test-user',
  subject_id: 'biology-101',
  material_type: 'pdf', // pdf, url, text, image
  material_source: 'https://example.com/biology-textbook.pdf',
  material_content: null, // For direct text input
  material_base64: null, // For file uploads
  chapter_name: 'Cell Biology',
  topics: ['mitochondria', 'cell membrane', 'nucleus'],
  difficulty_level: 3,
  learning_objectives: [
    'Understand cell structure',
    'Identify organelle functions',
    'Explain cellular processes'
  ]
};

// Only use defaults if coming from manual trigger
const isManual = $execution.resumeUrl === undefined;
if (isManual) {
  return [{json: defaults}];
}

// Otherwise pass through webhook data
return items;

// Prepare the material for RAG processing
const input = items[0].json;

// Create unique collection name for this user's subject
const collectionName = `study_buddy_${input.user_id}_${input.subject_id}`.replace(/[^a-zA-Z0-9_]/g, '_');

// Prepare metadata for the chunks
const metadata = {
  user_id: input.user_id,
  subject_id: input.subject_id,
  chapter: input.chapter_name || 'General',
  topics: input.topics || [],
  difficulty: input.difficulty_level || 3,
  learning_objectives: input.learning_objectives || [],
  upload_date: new Date().toISOString(),
  material_type: input.material_type
};

// Determine input type and source
let ragInput = {
  collection_name: collectionName,
  metadata: metadata,
  chunk_size: 500, // Smaller chunks for study materials
  chunk_overlap: 100,
  generate_summary: true,
  extract_entities: true // Extract key terms, definitions, etc.
};

if (input.material_type === 'url') {
  ragInput.input_type = 'url';
  ragInput.url = input.material_source;
} else if (input.material_type === 'text') {
  ragInput.input_type = 'text';
  ragInput.text = input.material_content || input.material_source;
} else if (input.material_type === 'pdf' || input.material_type === 'image') {
  ragInput.input_type = 'base64';
  ragInput.base64_content = input.material_base64;
  ragInput.file_type = input.material_type;
} else if (input.material_source && input.material_source.startsWith('/')) {
  ragInput.input_type = 'file';
  ragInput.file_path = input.material_source;
}

return [{json: ragInput}];

// Process the RAG results and generate study materials
const ragResult = items[0].json;
const originalInput = $input.all()[0].json;

// Extract key information from the processed material
const processedChunks = ragResult.chunks || [];
const summary = ragResult.summary || 'Material processed successfully';
const entities = ragResult.entities || [];

// Identify key concepts and definitions
const keyConcepts = entities.filter(e => 
  e.type === 'CONCEPT' || 
  e.type === 'TERM' || 
  e.type === 'DEFINITION'
).slice(0, 20); // Top 20 concepts

// Prepare response
const response = {
  success: true,
  user_id: originalInput.user_id,
  subject_id: originalInput.subject_id,
  material_id: `material_${Date.now()}`,
  collection_name: ragResult.collection_name,
  processing_summary: {
    chunks_created: processedChunks.length,
    vectors_stored: ragResult.vectors_stored || processedChunks.length,
    summary: summary,
    key_concepts: keyConcepts,
    topics_covered: originalInput.topics || []
  },
  study_recommendations: {
    estimated_study_time: Math.ceil(processedChunks.length * 2), // 2 min per chunk
    flashcards_to_generate: Math.min(50, keyConcepts.length * 3),
    quiz_questions_available: Math.ceil(processedChunks.length / 5),
    difficulty_level: originalInput.difficulty_level || 3
  },
  next_steps: [
    'Generate flashcards from key concepts',
    'Create quiz based on learning objectives',
    'Build study plan for systematic review'
  ],
  timestamp: new Date().toISOString()
};

return [{json: response}];

